mapper.select:
		SELECT DONG_NAME 
   		FROM LOCATION 
   		WHERE SIDO_NAME = #{sido}
   			  AND SIGUNGU_NAME = #{sigungu}
		GROUP BY DONG_NAME 
		ORDER BY DONG_NAME

mapper.insert:
INSERT ALL
INTO PRODUCT_FILE VALUES(SSFILE_COUNT, SEQ_PCOUNT.CURRVAL, #{ssf.originalFileName}, #{ssf.changeFileName},
DEFAULT
#{ssf.status}
)
SELECT * FROM DUAL

mapper.update:
UPDATE PRODUCT_FILE SET STATUS = 'N'
WHERE PRODUCT_NUM = #{ssNum} 

	  AND FILE_ID NOT IN

	  		#{fileId}

UPDATE PRODUCT SET CATEGORY = #{category}, PRODUCT_NAME= #{productName}, PD_CONTENT = #{pdContent}, PD_TITLE = #{pdTitle},
				   PRICE = #{price}, SIDO_NAME = #{addr1}, SIGUNGU_NAME = #{addr2}, DONG_NAME = #{addr3}
WHERE STATUS = 'Y' AND PRODUCT_NUM = #{productNum} AND MEMBER_ID = #{memberId}

INSERT ALL
INTO PRODUCT_FILE VALUES(SSFILE_COUNT, #{productNum}, #{ssf.originalFileName}, #{ssf.changeFileName},
DEFAULT
#{ssf.status}
)
SELECT * FROM DUAL

UPDATE PRODUCT
SET STATUS='N'
WHERE PRODUCT_NUM = ${ssNum}

UPDATE PRODUCT_COMMENT
SET COMMENT_CONTENT = #{c_content}, MODIFY_DATE = SYSDATE
WHERE COMMENT_NUM = #{c_num}

UPDATE PRODUCT_COMMENT 
SET STATUS = 'N'
WHERE COMMENT_NUM = #{cNum}
